import React, { useState } from 'react';

const Navbar = () => {
      const [isOpen, setIsOpen] = useState(false);

      const menu = (e) => {
            let list = document.querySelector('ul');

            if (e.name === 'menu') {
                  e.name = "close";
                  list.classList.add('top-[80px]');
            } else {
                  e.name = 'menu';
                  list.classList.remove('top-[80px]');
            }
      };

      return (
            <nav className="md:py-3 App home py-10 rounded-lg px-10">
                  <div className="container">
                        <div className="justify-between text-black md:flex">
                              <div className="flex space-x-4 pt-2 justify-between">
                                    <img src="images/m.jpg" className="mx-1 h-8 rounded-full" alt="Avatar" />
                                    <h1 className="pt-1">MITHILESH KUMAAR.</h1>
                                    <span className="cursor-pointer md:hidden text-3xl" onClick={menu}>
                                          <ion-icon name="menu"></ion-icon>
                                    </span>
                              </div>
                              <div>
                                    <ul className="md:flex md:item-center z-[-1] md:z-auto md:static absolute w-full md:auto md:opacity-100 opacity-0 top-[-400px] transition-all ease-in duration-500 bgcolor">
                                          <li>
                                                <a href="#home">Home</a>
                                          </li>
                                          <li>
                                                <a href="#about">About.Me</a>
                                          </li>
                                          <li>
                                                <a href="#services">Services</a>
                                          </li>
                                          <li>
                                                <a href="#projects">Projects</a>
                                          </li>
                                          <li>
                                                <a href="#contacts">Contacts</a>
                                          </li>
                                    </ul>
                              </div>
                        </div>
                  </div>
            </nav>
      );
};

export default Navbar;
