import React, { useState } from 'react';

function Menu() {
      const [menuState, setMenuState] = useState('menu');

      const toggleMenu = () => {
            setMenuState(menuState === 'menu' ? 'close' : 'menu');
      };

      return (
            <ul className={`md:flex md:item-center z-[-1] md:z-auto md:static absolute w-full md:auto md:opacity-100 ${menuState === 'menu' ? 'opacity-0 top-[-400px]' : 'opacity-100'} transition-all ease-in duration-500 bgcolor`}>
                  <li>
                        <a href='#home'>Home</a>
                  </li>
                  <li>
                        <a href='#about'>About.Me</a>
                  </li>
                  <li>
                        <a href='#services'>Services</a>
                  </li>
                  <li>
                        <a href='#projects'>Projects</a>
                  </li>
                  <li>
                        <a href='#contacts'>Contacts</a>
                  </li>
            </ul>
      );
}

const Navbar = () => {
      const [isOpen, setIsOpen] = useState(false);

      return (
            <nav className="md:py-3 App home py-10 rounded-lg px-10 ">
                  <div className="container">
                        <div className='justify-between text-black md:flex  '>
                              <div className='flex space-x-4 pt-2'>
                                    <img src="images/m.jpg" className=' mx-1 h-8 rounded-full' />
                                    <h1 className='pt-1'>MITHILESH KUMAAR.</h1>
                                    <span className='cursor-pointer md:hidden ' onClick={() => setIsOpen(!isOpen)}>
                                          <ion-icon name="menu-outline"></ion-icon>
                                    </span>
                              </div>
                              <div>
                                    {isOpen && <Menu />}
                              </div>
                        </div>
                  </div>
            </nav>
      );
};

export default Navbar;
